files.contents:
.order-wizard-promocodeform{
	clear:both;
	display:block;
	overflow:hidden;
	margin-bottom:$sc-margin-lv2;

	@media(min-width: $screen-sm-min){
		margin-bottom:$sc-margin-lv4;
	}
}



.order-wizard-promocodeform-expander-head-toggle{
	@extend .accordion-head-toggle-secondary;
}

.order-wizard-promocodeform-expander-toggle-icon{
	@extend .accordion-toggle-icon-secondary;
}

.order-wizard-promocodeform-expander-body{
	@extend .accordion-body-alt;
}
.order-wizard-promocodeform-tooltip{
	@extend .order-wizard-promocode-tooltip;
}

.order-wizard-promocodeform-unsupported-summary-warning{
	padding:$sc-padding-lv3;
}

.order-wizard-register-guest-module-create-account-button{
	@extend .button-primary;
	@extend .button-large;
	margin-bottom:$sc-margin-lv4;
}

.order-wizard-register-guest-module-button-toggle-create-account{
	@extend .button-medium;
	@extend .button-secondary;
	margin-bottom:$sc-margin-lv4;
}

.order-wizard-registeremail-module-show-addresses-container {
	@extend .box-column;
	overflow: hidden;
}

.order-wizard-registeremail-module-title {
	margin-bottom: $sc-margin-lv2;
}



.order-wizard-registeremail-module-edit-fields-group-input {
	@extend .input-large;
}

.order-wizard-registeremail-module-checkbox {
	margin-bottom: $sc-margin-lv4;
	display: block;
}

.order-wizard-registeremail-module-input-required {
	@extend .input-required;
}

.order-wizard-registeremail-module-input-help {
	@extend .help-text;
}

.order-wizard-msr-shipmethod-module-message-icon{
	@extend .order-wizard-msr-shipmethod-package-message-icon;
}

.order-wizard-msr-shipmethod-package{
	@extend .box-column;
}

.order-wizard-msr-shipmethod-package-row{
	@extend .row;
}

.order-wizard-msr-shipmethod-package-address,
.order-wizard-msr-shipmethod-package-delivery-method{
	@extend .col-xs-12;
	@extend .col-sm-6;
}

.order-wizard-msr-shipmethod-package-delivery-method{
	margin-top: $sc-margin-lv4;
	@media (min-width: $screen-sm-min){
		margin-top: 0;
	}

	.order-wizard-msr-shipmethod-package-delivery-option {
		min-height: inherit;
	}
}

.order-wizard-msr-shipmethod-package-title{
	margin-bottom: $sc-margin-lv2;
}



.order-wizard-msr-shipmethod-package-address-detail,
.order-wizard-msr-shipmethod-package-delivery-option{
	@extend .info-card;
}

.order-wizard-msr-shipmethod-package-shipmethod-rate{
	float: right;
	margin-left: $sc-margin-lv2;
}

.order-wizard-msr-shipmethod-package-message{
	@extend .message-warning;
}

.order-wizard-msr-shipmethod-package-message-icon{
	@extend .icon-base;
	@extend %fa-exclamation-triangle;
}

.order-wizard-msr-shipmethod-package-items{
	margin-top: $sc-margin-lv4;
}

.order-wizard-msr-shipmethod-package-accordion-head-toggle{
	@extend .accordion-head-toggle;
	@extend .small-title;
	text-transform: none;
}

.shopping-cart-summary-expander-toggle-icon{
	@extend .accordion-toggle-icon;
}

.order-wizard-msr-shipmethod-package-accordion-body{
	@extend .accordion-body;

	td{
		text-align: left;
	}

	.item-view-cell-navigable-item-image{
		padding-left:0;
	}
}

.order-wizard-msr-shipmethod-package-edit-shipment{
	display:block;
	margin: 0 0 $sc-margin-lv3;
	padding: $sc-padding-lv3 $sc-padding-lv4;
	border-bottom: 1px solid $sc-color-theme-border-light;
}

.order-wizard-msr-shipmethod-package-address-link {
	margin-top: $sc-margin-lv3;
	margin-bottom: $sc-margin-lv3;
	display: inline-block;
	@media (min-width: $screen-md-min) {
		margin-bottom: 0;
	}
}

.order-wizard-msr-shipmethod-package-accordion-body-wrapper{
	@extend .accordion-container;
	margin-top: 0;
}

.order-wizard-msr-shipmethod-package-item-count-label {
	@extend .info-card;
}
.order-wizard-msr-shipmethod-package-accordion-container-table {
	@extend .table-flex-item-navigable;
}

.order-wizard-msr-shipmethod-package-accordion-header-icon{
	@extend .shopping-cart-summary-expander-toggle-icon;
}
.order-wizard-msr-shipmethod-package-accordion-container-table-header-quantity{
	text-align: right;
}

.order-wizard-shipmethod-module{
	@extend .box-column;
}

.order-wizard-shipmethod-module-title{
	margin-bottom: $sc-margin-lv4;
}

.order-wizard-shipmethod-module-message{
	@extend .message-warning;
}

.order-wizard-shipmethod-module-option-select{
	@extend .input-large;
}

.order-wizard-shipmethod-module-option{
	color: $sc-color-copy;
	display: block;
	padding: $sc-padding-lv5;
	margin-bottom: $sc-margin-lv2;
	border: 1px solid $sc-color-theme-border;
	cursor: pointer;
}

.order-wizard-shipmethod-module-option-active {
	background-color: $sc-neutral-shade-0;
}

.order-wizard-shipmethod-module-checkbox{
	margin-right: $sc-margin-lv2;
}

.order-wizard-shipmethod-module-option-price{
	float: right;
}

.order-wizard-msr-shipmethod-module-message{
	@extend .message-warning;
}

.order-wizard-msr-shipmethod-package-message-icon{
	@extend .icon-base;
	@extend %fa-exclamation-triangle;
}

.order-wizard-showshipments-actionable-module-container{
	@extend .box-column;
}

.order-wizard-showshipments-actionable-module-shipping-address{
	@extend .col-xs-12;
	@extend .col-sm-6;
}

.order-wizard-showshipments-actionable-module-shipping-method{
	@extend .col-xs-12;
	@extend .col-sm-6;
}
.order-wizard-showshipments-actionable-module-shipping-details{
	@extend .box-column;
	margin-bottom: $sc-margin-lv4;
}
.order-wizard-showshipments-actionable-module-cart-details{
	margin: 0 0 $sc-margin-lv4;
}
.order-wizard-showshipments-actionable-module-shipping-details-body {
	@extend .row;
}
.order-wizard-showshipments-actionable-module-shipping-details-address,
.order-wizard-showshipments-actionable-module-shipping-details-method {
	@extend .col-sm-6;
}
.order-wizard-showshipments-actionable-module-shipping-details-address {
	margin-bottom: $sc-margin-lv3;

  @media (min-width: $screen-sm-min){
  	margin-bottom: 0
  }
	.address-details-info {
		margin-bottom: 0;
	}
}
.order-wizard-showshipments-actionable-module-shipping-details-address-link {
	margin-top: $sc-margin-lv3;
	display: inline-block;
}
.order-wizard-showshipments-actionable-module-shipping-title {
	margin-bottom: $sc-margin-lv4;
}


.order-wizard-showshipments-actionable-module-shipping-details-method-info-card {
	@extend .info-card;
}
.order-wizard-showshipments-actionable-module-cart-details-accordion-head{
	@extend .accordion-head;
}
.order-wizard-showshipments-actionable-module-cart-details-accordion-head-toggle{
	@extend .accordion-head-toggle;
}
.order-wizard-showshipments-actionable-module-cart-details-accordion-toggle-icon{
	@extend .accordion-toggle-icon;
}
.order-wizard-showshipments-actionable-module-cart-details-accordion-body{
	@extend .accordion-body;
}
.order-wizard-showshipments-actionable-module-accordion-container{
	padding: $sc-padding-lv4;
	.transaction-line-views-cell-actionable-options {
		padding-left: 0;
	}
}

.order-wizard-showshipments-module-container{
	@extend .box-column;
}

.order-wizard-showshipments-module-shipping-address{
	@extend .col-xs-12;
	@extend .col-sm-6;
}

.order-wizard-showshipments-module-shipping-method{
	@extend .col-xs-12;
	@extend .col-sm-6;
}
.order-wizard-showshipments-module-shipping-details{
	@extend .box-column;
}

.order-wizard-showshipments-module-shipping-details-body {
	@extend .row;
}
.order-wizard-showshipments-module-shipping-details-address{
	@extend .col-md-4;
}
.order-wizard-showshipments-module-shipping-details-items {
	@extend .col-md-8;
}
.order-wizard-showshipments-module-shipping-details-address {
	margin-bottom: $sc-margin-lv4;

	.address-details {
		border: 1px solid $sc-color-theme-border-light;
	}
	.address-details-info {
		margin-bottom: 0;
	}
}
.order-wizard-showshipments-module-shipping-details-method {
	.order-wizard-showshipments-module-shipping-details-method-info-card {
		min-height: inherit;
	}

	.order-wizard-showshipments-module-shipping-title {
		margin-left: 0;
	}
}
.order-wizard-showshipments-module-shipping-details-address-link {
	margin-bottom: $sc-margin-lv3;
	display: inline-block;
}
.order-wizard-showshipments-module-shipping-title
 {
	margin-bottom: $sc-margin-lv4;
	margin-left: $sc-margin-lv3;
}

.order-wizard-showshipments-module-edit-cart-link{

	display: block;
	padding: $sc-padding-lv3 $sc-padding-lv4;
	margin: - $sc-margin-lv4;
	margin-bottom:$sc-margin-lv3;
	border-bottom:1px solid $sc-color-theme-border-light;
}

.order-wizard-showshipments-module-shipping-options{
	width: 100%;
}

.order-wizard-showshipments-module-shipping-details-method-info-card {
	@extend .info-card;
}

.order-wizard-showshipments-module-shipping-details-address-view {
	margin-bottom: $sc-margin-lv4;

	.address-details-info * {
	    font-size: $sc-font-size-s;
	    line-height: 1.6;
	}

	.address-details-edit-address {
		margin: 0;
	}
}

.order-wizard-submitbutton-container {
	@extend .col-xs-12;
	padding:0;
	margin-bottom:$sc-padding-base * 4;
	display:none;
	float: none;

	@media (min-width: $screen-sm-min) {
		display:block;
	}

}
.order-wizard-submitbutton-module-top{
	margin-top: $sc-margin-lv2;
	margin-bottom: $sc-margin-lv4;
	@media (min-width: $screen-sm-min){
		display: none;
	}
}

.order-wizard-submitbutton-module-button{
	@extend .button-primary;
	@extend .button-large;
	@extend .button-generic;
}

.order-wizard-termsandconditions-module-top-summary {
	display:none;
	margin-bottom: $sc-margin-lv4;
	@media(min-width: $screen-sm-min){
		display: block;
	}
}
.order-wizard-termsandconditions-module-top,
.order-wizard-termsandconditions-module-bottom{
	@media(min-width: $screen-sm-min){
		display: none;
	}
}

.order-wizard-termsandconditions-module-bottom{
	margin-top: $sc-margin-lv4;
}

.order-wizard-termsandconditions-module-default{
	display: none;
	@media(min-width: $screen-sm-min){
		display: block;
		float:right;
		margin-bottom: $sc-margin-lv4;
	}
}

{{#if showWrapper}}
<div class="{{wrapperClass}}">
{{/if}}
	<label class="order-wizard-termsandconditions-module-label">
		<input type="checkbox" id="termsandconditions" name="termsandconditions" {{#if isAgreeTermCondition}}checked{{/if}}>
		{{translate 'I agree to the <a data-type="term-condition-link-module" data-toggle="show-terms" href="#">Terms & Conditions</a>'}}
	</label>
{{#if showWrapper}}
</div>
{{/if}}

