.order-wizard-title{
	margin-bottom: $sc-margin-lv4;
}

.order-wizard-step-header{
	@extend .wizard-step-header;
}

.order-wizard-step-review-wrapper,
.order-wizard-step-content-wrapper{
	@extend .row;
}

.order-wizard-step-review-main,
.order-wizard-step-content-main{
	@extend .col-xs-12;
	@extend .col-sm-7;
	@extend .col-md-9;
}

.order-wizard-step-review-secondary,
.order-wizard-step-content-secondary{
	@extend .col-xs-12;
	@extend .col-sm-5;
	@extend .col-md-3;
	float: right;
}

.order-wizard-step-content-secondary{
	margin-bottom: $sc-margin-lv4;
}

.order-wizard-step-actions{
	@extend .col-xs-12;
	@extend .col-sm-7;
	@extend .col-md-9;
}

.order-wizard-step-message{
	@extend .wizard-step-message;

	margin-bottom: $sc-margin-lv6 !important;
}

.order-wizard-step-button-container{
	@extend .wizard-step-button-container !optional;
}

.order-wizard-step-button-continue {
	@extend .wizard-step-button-continue;
	margin-bottom: $sc-margin-lv2;
}

.order-wizard-step-button-back{
	@extend .wizard-step-button-back;
}

.order-wizard-step-skip-login-register {
		margin-left: 15px;
}

.ProfileHomeStandaloneView {

    h2 {
      margin-bottom: $sc-margin-lv3;
    }
	.overview-home-invoices-header {

		border-bottom: 1px solid $sc-color-theme-border-light;
		border-top: 1px solid $sc-color-theme-border-light;
		display: inline-block;
		padding: $sc-padding-lv3 0;
		width: 100%;

		hr {
			margin: $sc-margin-lv3 0;
		}

		h3 {
			@media (min-width: $screen-sm-min) {
				float: left;
			}
		}

		.overview-home-anchor-invoices-list {
			float: right;
			margin-right: $sc-margin-lv3;
		}
	}

	.overview-home-invoices-results-container {

		.overview-home-invoices-list-table {

			@extend .table-flex-record-navigable;

			.overview-home-content-table {

				@extend .recordviews-row-header;

				.overview-home-content-table-header-row {

					@extend .recordviews-table;

					.overview-home-content-table-header-row-title {
						text-align: left;
						width: 50%;
					}

					.overview-home-content-table-header-row-date {
						text-align: left;
						width: 15%;
					}

					.overview-home-content-table-header-row-status {
						text-align: left;
						width: 15%;
					}
				}
			}
		}

		@media (min-width: $screen-md-min) {

			td {
				text-align: left;
				vertical-align: middle;
			}


			.overview-home-content-table-header-row-title {
				width: 20%;
			}

			.overview-home-content-table-header-row-date {
				width: 15%;
			}

			.overview-home-content-table-header-row-status {
				width: 15%;
			}
		}
	}

	.overview-home-invoices-empty-list {
		margin: $sc-margin-lv2 0 $sc-margin-lv4;
	}

	.overview-home-invoices-empty-section{
		padding:$sc-padding-lv8 $sc-padding-lv3 $sc-padding-lv3 $sc-padding-lv3;
		text-align:center;
	}
	.overview-home-mysettings {
		@extend .address-edit;
		margin-top: $sc-margin-lv1;
		@extend .row;

		@media (min-width: $screen-sm-min) {
			margin: $sc-margin-lv5 0 0;
		}
	}
	.overview-home-mysettings-row {
		@extend .row;
		margin-top: $sc-margin-lv3;
	}

	.overview-home-mysettings-profile,
	.overview-home-mysettings-shipping,
	.overview-home-mysettings-payment {
		@extend .col-md-4;
		@extend .col-sm-6;
	}
}

.overview-home-orders-title {
	padding: $sc-padding-lv3 0;
	border-bottom: 1px solid $sc-color-theme-border-light;
	border-top: 1px solid $sc-color-theme-border-light;
	display: inline-block;
	width: 100%;
	h3 {
		@media (min-width: $screen-sm-min) {
			float: left;
		}
	}
}
.overview-home-orders-title-link {
	margin-top: $sc-margin-lv1;
	font-size:14px;

	@media (min-width: $screen-sm-min) {
		float: right
	}
}
.overview-home-orders-empty-list {
	margin: $sc-margin-lv2 0 $sc-margin-lv4;
}
.overview-home-mysettings {
	@extend .address-edit;
	margin-top: $sc-margin-lv1;
	@extend .row;

	@media (min-width: $screen-sm-min) {
		margin: $sc-margin-lv5 0 0;
	}
}
.overview-home-mysettings-row {
	@extend .row;
	margin-top: $sc-margin-lv3;
}
.overview-home-mysettings-profile,
.overview-home-mysettings-shipping,
.overview-home-mysettings-payment {
	@extend .col-md-4;
	@extend .col-sm-6;
}

//LIST

.overview-home-content-table {
	@extend .recordviews-row-header;
}

.overview-home-content-table-header-row {
	@extend .recordviews-table;
}

.overview-home-content-table-header-row-title {
	@extend .recordviews-header !optional;
	width: 20%;
}
.overview-home-content-table-header-row-date {
	@extend .recordviews-date-header;
	width: 15%;
}
.overview-home-content-table-header-row-currency {
	@extend .recordviews-currency-header;
	width: 20%;
}
.overview-home-content-table-header-row-origin {
	@extend .recordviews-status-header;
	width: 25%;
}
.overview-home-content-table-header-row-status {
	@extend .recordviews-status-header;
	width: 25%;
}
.overview-home-content-table-header-row-track {
	@extend .recordviews-header !optional;
	width: 20%;

	@media (min-width: $screen-md-min) {
		text-align: right;
	}
}

.overview-home-order-history-results-container {
	.recordviews-tracking-number-header	{
		color: $sc-color-copy-dark;
	}

	.recordviews-tracking-number {
		color: $sc-color-primary;
		padding-bottom: $sc-padding-lv3;
	}

	@media (min-width: $screen-sm-min) and (max-width: $screen-sm-max) {

		.recordviews-tracking-number-header{
			width: 50%;
			text-align: right;
			padding-right: $sc-padding-lv3;
		}
	}

	@media (min-width: $screen-md-min) {
		.recordviews-tracking-number-header,
		.recordviews-tracking-number {
			 width: 20%;
			 text-align: right;
		}

		.recordviews-title {
			width: 20%;
		}
		.recordviews-date-header,
		.recordviews-date {
			width: 15%;
		}
		.recordviews-currency-header,
		.recordviews-currency {
			width: 20%;
		}
		.recordviews-status-header,
		.recordviews-status {
			width: 25%;
		}
	}
}

.overview-home-header-links{
	@extend .pull-right;
}


.overview-home-orders-list-table{
	@extend .table-flex-record-navigable;

	.overview-home-content-table-header-row-origin,
	.recordviews-origin{
		width:100%;
	}

	.overview-home-content-table-header-row-currency,
	.recordviews-currency{
		width:100%;
	}

	.overview-home-content-table-header-row-date,
	.recordviews-date{
		width:100%;
	}

	.overview-home-content-table-header-row-title,
	.recordviews-title{
		width:100%;
	}

	.overview-home-content-table-header-row-track,
	.recordviews-tracking-number {
		width:100%;
	}

	@media (min-width: $screen-md-min) {
		td {
			vertical-align:middle;
		}
		.overview-home-content-table-header-row-origin,
		.recordviews-origin{
			width:15%;
		}

		.overview-home-content-table-header-row-currency,
		.recordviews-currency{
			width:20%;
		}

		.overview-home-content-table-header-row-date,
		.recordviews-date{
			width:10%;
		}

		.overview-home-content-table-header-row-title,
		.recordviews-title{
			width:20%;
		}

		.overview-home-content-table-header-row-track,
		.recordviews-tracking-number {
			width:35%;
		}
	}

}



.overview-home-orders-empty-section{
	padding:$sc-padding-lv8 $sc-padding-lv3 $sc-padding-lv3 $sc-padding-lv3;
	text-align:center;
}

.overview-payment-header {
    @extend .overview-shipping-header;
}
.overview-payment-header h4 {
    margin: $sc-margin-lv2 0;
}
.overview-payment-card {
   	@extend .overview-shipping-card;
}
.overview-payment-card .creditcard {
	@extend .overview-shipping-card-content;

	margin: 0;
	border: none;

	.creditcard-container {
		padding: 0;
		border: 0;
	}
}

.overview-payment-card-button-edit {
    @extend .overview-shipping-card-button-edit;
}
.overview-payment-card-content {
	@extend .overview-shipping-card-content;
	@extend .info-card;
	width: 100%;
	border: none;
	padding: 0px;
}

.overview-payment-card-button-edit-container {
	display: block;
	text-align: right;
}

.overview-profile-header {
    @extend .overview-shipping-header;
}
.overview-profile-header h4 {
    margin: $sc-margin-lv2 0;
}
.overview-profile-card {
    @extend .overview-shipping-card;
}
.overview-profile-card-content {
	@extend .overview-shipping-card-content;
}
.overview-profile-card-button-edit {
    @extend .overview-shipping-card-button-edit;
}
.overview-profile-company, .overview-profile-name-title {
	font-weight: $sc-font-weight-bold;
}

.overview-profile-card-button-edit-container {
	display: block;
	text-align: right;
}

.overview-shipping-header {
  display: inline-block;
}
.overview-shipping-header h4 {
  margin: $sc-margin-lv2 0;
}
.overview-shipping-card {
  @extend .info-card;
  width: 100%;
}
.overview-shipping-card .address-details {
  border: 0;

  .address-details-container {
    padding: 0;
  }
}
.overview-shipping-card-content {
	@media (min-width: $screen-sm-min) {
